import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { deleteVideo, setVideo } from '../actions/ListAction'
import { videoService } from '../services/videoService'
import { FavoritePreview } from './FavoritePreview'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd';


export function FavoritesList() {
    const { favorites } = useSelector(state => state.listModule)
    const [list, setlist] = useState([])
    const dispatch = useDispatch()

    useEffect(() => {
        console.log('im loading');
        loadFavorites()
    }, [favorites])


    async function loadFavorites() {
        const favorites = await videoService.queryFavorites()
        setlist(favorites)
    }

    function setCurrVideo(ev, video) {
        ev.stopPropagation()
        dispatch(setVideo(video))
    }

    const deleteFromList = (ev, video) => {
        ev.stopPropagation()
        deleteVideo(video)
        const newList = videoService.deleteFromFavorites(list, video.id)
        setlist(newList)
    }

    return (
        <div>
            <DragDropContext>
                <Droppable droppableId="favoList" type="favoritesList">
                    {(provided) => {
                        <div className="favorites-container" {...provided.droppableProps} ref={provided.innerRef}>
                            {list.map((video, index) => {
                                return (
                                    <Draggable key={video.id.videoId} draggableId={video.id.videoId} index={index}>
                                        {(provided) => {
                                            return (
                                                <div ref={provided.innerRef} {...provided.draggableProps}  {...provided.dragHandleProps}>
                                                    <FavoritePreview
                                                        key={video.id.videoId}
                                                        video={video}
                                                        deleteFromList={deleteFromList}
                                                        setCurrVideo={setCurrVideo} />
                                                </div>
                                            )

                                        }}
                                    </Draggable>
                                )
                            })}
                            {provided.placeholder}
                        </div>
                    }}
                </Droppable>
            </DragDropContext>
        </div>
    )
}
