import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { deleteVideo, setVideo } from '../actions/ListAction'
import { videoService } from '../services/videoService'
import { FavoritePreview } from './FavoritePreview'
import { DragDropContext, Draggable, Droppable } from 'react-beautiful-dnd';


export function FavoritesList() {
    const { favorites } = useSelector(state => state.listModule)
    const [list, setlist] = useState([])
    const dispatch = useDispatch()

    useEffect(() => {
        console.log('im loading');
        loadFavorites()
    }, [favorites])


    async function loadFavorites() {
        const favorites = await videoService.queryFavorites()
        setlist(favorites)


    }

    function setCurrVideo(ev, video) {
        ev.stopPropagation()
        dispatch(setVideo(video))
    }

    const deleteFromList = (ev, video) => {
        ev.stopPropagation()
        deleteVideo(video)
        const newList = videoService.deleteFromFavorites(list, video.id)
        setlist(newList)
    }
    const handleDragEnd = res => {
        console.log('res is:', res);
    }
    console.log('list is:', list);
    return (
        <section>
            <DragDropContext onDragEnd={handleDragEnd}>
                <Droppable droppableId="droppable" type="favorite">
                    {(provided) => {
                        <div className="list" {...provided.droppableProps} ref={provided.innerRef} >
                            {list.map((video, index) => {
                                return (
                                    <Draggable key={video.id.videoId} draggableId={index} index={index}>
                                        {(provided) => {
                                            return (
                                                <div className="favorite-preview flex" >
                                                    <div {...provided.dragHandleProps} {...provided.draggableProps} ref={provided.innerRef}>
                                                        <img className="video-img" src={video.snippet.thumbnails.medium.url} alt="img-video" />
                                                        <div className="video-content flex column" >
                                                            <h4>{video.snippet.title}</h4>
                                                            <p>{video.snippet.description}</p>
                                                            <div className="btn-div">
                                                                {/* <button className="play" onClick={(ev) => playCurrVideo(ev, video)}>Play</button> */}
                                                                {/* <button className="delete" onClick={(ev) => deleteFromList(ev, video)}>Delete</button> */}
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            )
                                            // <div  {...provided.draggableProps} ref={provided.innerRef}  >
                                            //     <FavoritePreview
                                            //         key={video.id.videoId}
                                            //         provided={provided}
                                            //         video={video}
                                            //         deleteFromList={deleteFromList}
                                            //         setCurrVideo={setCurrVideo} />
                                            // </div>
                                        }}
                                    </Draggable>
                                )
                            })}
                            {provided.placeholder}
                        </div>
                    }}
                </Droppable>
            </DragDropContext>
        </section>
    )
}
