import React, { useEffect } from 'react'
import { useSelector } from 'react-redux';
import YouTube from 'react-youtube';
import { storageService } from '../services/storageService';
// import { videoService } from '../services/videoService';

export function SelectedVideo({ video, saveToFavorites }) {
    const { favorites } = useSelector(state => state.listModule)
    const opts = {
        height: '510px',
        width: '100%',
        playerVars: {
            autoplay: 1,
        },
    }

    const _onReady = (event) => {
        event.target.pauseVideo();
    }

    useEffect(() => {
        console.log('isFavorites is:', favorites);

    }, [favorites])

    function date() {
        const date = video.snippet?.publishTime
        console.log('date is:', date.split('T')[0]);
    }




    if (!video) return <div>Loading..</div>
    return (

        <div className="video-player">
            <YouTube videoId={video.id?.videoId} opts={opts} onReady={_onReady} />
            <div className="info">
                <h1>{video.snippet?.title}</h1>
                <p>{video.snippet?.description}</p>
                <p>Date: {date()}</p>
                <button
                    className="star"
                    onClick={() => saveToFavorites(video)}>
                    <span className={video.favorite ? 'favorite' : ''}>â˜†</span>
                </button>
            </div>
        </div >

    )
}
