import { VideoList } from '../cmps/VideoList';
import { videoService } from '../services/videoService.js'
import { setVideosList } from '../actions/ListAction'
import { useEffect, useState } from 'react';
import { EmbeddedVideo } from '../cmps/EmbeddedVideo';
import { NavBar } from '../cmps/NavBar';
import { useDispatch, useSelector } from 'react-redux';

export function VideoApp() {
    const dispatch = useDispatch()
    const { list } = useSelector(state => state.listModule)

    const [keyword, setKeyword] = useState('')
    const [video, setVideo] = useState('')


    // const [videos, setVideos] = useState([])

    useEffect(() => {
        // loadVideos()
        // if (videoList.length) setCurrVideo(videoList[0])
        console.log('ami is:');
        // setVideos(list)
    }, [list])

    async function loadVideos(ev = null) {
        if (ev)
            ev.preventDefault();
        dispatch(setVideosList(keyword))
    }
    const setCurrVideo = (video) => { dispatch(setVideo(video)) }
    return (
        <section>
            <header className="app-header">
                <NavBar loadVideos={loadVideos} setKeyword={setKeyword} keyword={keyword} />

            </header>
            <div className="video-app flex">
                <VideoList videos={list} setCurrVideo={setCurrVideo} />
                <EmbeddedVideo video={video} />
            </div>
        </section>
    )
}
