import React from 'react'
import { useHistory } from 'react-router-dom'

export function FavoritePreview({ video, setCurrVideo }) {
    const history = useHistory()
    return (
        <div
            onClick={() => { return (setCurrVideo(video), history.push('/play')) }}
            className="favorite-preview flex">
            <img className="video-img" src={video.snippet.thumbnails.medium.url} alt="img-video" />
            <div className="video-content flex column">
                <h4>{video.snippet.title}</h4>
                <p>{video.snippet.description}</p>

            </div>
        </div>
    )
}
