    const videos = res.data;
    storageService.saveToStorage(VIDEOS_KEY, videos)
    storageService.saveToStorage(KEYWORD_KEY, keyword)
    return videos;
}
async function queryFavorites() {
    try {
        const res = await axios.get(BASE_URL);
        const favorites = res.data;
        storageService.saveToStorage(FAVORITES_LIST, favorites)
        return favorites;
    } catch {
        console.log('soryyy i cant to query favorites');
    }

}

function chackIsFavorites(list, videoToChack) {
    const isFavorites = list.some(video => video.id.videoId === videoToChack.id?.videoId)
    if (isFavorites) return true
    else return false
}

async function saveVideoToFavorites(video) {
    video.favorite = true
    const res = await axios.post(BASE_URL, video);
    return res.data;
}

function deleteFromFavorites(list, videoId) {
    console.log('list,videoId is:', list, videoId);
    const idx = list.findIndex(video => video.id.videoId === videoId.videoId)
    console.log('idx is:', idx);
    list[idx].favorite = false
    list.splice(idx, 1)
    return list
}

async function removeVideo(video) {
    try {
        console.log('video is:', video);
        const res = await axios.delete(`${BASE_URL}/${video.id.videoId}`)
        return res.data
    }
    catch {
