import axios from "axios";
import { storageService } from './storageService.js'

const VIDEOS_KEY = 'video'
const KEYWORD_KEY = 'keyword'
const FAVORITES_VIDEOS = 'favorites'

const BASE_URL = 'http://localhost:3030/api/favorites';
export const videoService = {
    query,
    chackIsFavorites,
    historylist,
    deleteFromFavorites,
    queryFavorites
}

async function query(keyword) {
    console.log('keyword is:', keyword);
    var storageKeyword = storageService.loadFromStorage(KEYWORD_KEY)
    if (keyword === '' && !storageKeyword) keyword = 'Angular'

    if (keyword === storageKeyword || keyword === '') {
        return storageService.loadFromStorage(VIDEOS_KEY)
    }
    // var listFromStorge = storageService.loadFromStorage(keyword)

    // return listFromStorge
    const res = await axios.get(`https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=10&type=video&q=${keyword}&key=AIzaSyCKuaufnbpG6KWdX28y4gBz43wMxdjEa54`);
    const videos = res.data;
    storageService.saveToStorage(VIDEOS_KEY, videos)
    storageService.saveToStorage(KEYWORD_KEY, keyword)
    return videos;
}
async function queryFavorites() {
    // const favoFromStorge = storageService.loadFromStorage(FAVORITES_VIDEOS)
    // if (favoFromStorge) return Promise.resolve(favoFromStorge)
    // else return
    const res = await axios.get(BASE_URL);
    const favorites = res.data;
    window.theFavori = favorites;
    return favorites;
}

function chackIsFavorites(list, video) {
    const videoId = video.id
    const isFavorites = list.some(video => video.id === videoId)
    if (isFavorites) {
        const newList = deleteFromFavorites(list, videoId)
        console.log('newList is:', newList);
        storageService.saveToStorage(FAVORITES_VIDEOS, newList)
        return newList
    }
    else return false
    // return Promise.resolve(video)
}

function deleteFromFavorites(list, videoId) {
    const idx = list.findIndex(video => video.id === videoId)
    console.log('idx is:', idx);
    list[idx].favorite = false
    list.splice(idx, 1)
    return list
}

function historylist(history, video) {
    var copy = [...history]
    console.log('copy is:', copy);
    copy.push(video)
}
