import { VideoList } from '../cmps/VideoList';
import { saveVideo, setVideosList, updateFavorites } from '../actions/ListAction'
import { useEffect, useState } from 'react';
import { EmbeddedVideo } from '../cmps/EmbeddedVideo';
import { NavBar } from '../cmps/NavBar';
import { useDispatch, useSelector } from 'react-redux';
import { videoService } from '../services/videoService';
import { Toolbar } from '../cmps/Toolbar';
import { FavoritesList } from '../cmps/FavoritesList';
// import { Route, Switch } from 'react-router-dom';
// import { Home } from './Home'

export function VideoApp() {
    const dispatch = useDispatch()
    const { list, favorites } = useSelector(state => state.listModule)
    const [video, setVideo] = useState({})
    const [keyword, setKeyword] = useState('')
    // const [history, setHistory] = useState([])
    const [favoritesList, setFavoritesList] = useState(false)
    const [menu, setMenu] = useState(false)

    function toggleMenuBar() {
        setMenu(!menu)

    }
    async function loadVideos(ev = null) {
        if (ev)
            ev.preventDefault();
        dispatch(setVideosList(keyword))
    }
    const setCurrVideo = (video) => {
        // dispatch(setVideo(video.id.videoId))
        setVideo(video)

    }
    const saveToFavorites = (video) => {
        const newFavoritesList = videoService.chackIsFavorites(favorites, video)
        if (newFavoritesList) {
            console.log('is favorite', newFavoritesList);
            dispatch(updateFavorites(newFavoritesList))
        } else {
            const newVideo = video
            newVideo.favorite = true
            dispatch(saveVideo(newVideo))
        }
    }

    useEffect(() => {
        console.log('favor', favorites);
    }, [favorites])


    return (
        <section>
            <NavBar
                loadVideos={loadVideos}
                toggleMenuBar={toggleMenuBar}
                setKeyword={setKeyword}
                keyword={keyword}
            />
            <div className="container flex">
                {menu && <Toolbar
                    setFavoritesList={setFavoritesList}
                />}
                {favoritesList &&
                    <FavoritesList
                        setCurrVideo={setCurrVideo} />}

                {!favoritesList && <div className="video-app flex">
                    <VideoList list={list} setCurrVideo={setCurrVideo} />
                    <EmbeddedVideo video={video} saveToFavorites={saveToFavorites} />
                </div>}
            </div>

        </section>
    )
}
