import { VideoList } from '../cmps/VideoList';
import { setVideosList } from '../actions/ListAction'
import { useState } from 'react';
import { EmbeddedVideo } from '../cmps/EmbeddedVideo';
import { NavBar } from '../cmps/NavBar';
import { useDispatch, useSelector } from 'react-redux';

export function VideoApp() {
    const dispatch = useDispatch()
    const { list } = useSelector(state => state.listModule)
    const [video, setVideo] = useState({})
    const [keyword, setKeyword] = useState('')


    async function loadVideos(ev = null) {
        if (ev)
            ev.preventDefault();
        dispatch(setVideosList(keyword))
    }
    const setCurrVideo = (video) => {
        // dispatch(setVideo(video.id.videoId))
        setVideo(video)
    }

    return (
        <section>
            <header className="app-header">
                <NavBar loadVideos={loadVideos} setKeyword={setKeyword} keyword={keyword} />

            </header>
            <div className="video-app flex">
                <VideoList list={list} setCurrVideo={setCurrVideo} />
                <EmbeddedVideo video={video} />
            </div>
        </section>
    )
}
