                            // {(provided) => (
                            // <div ref={provided.innerRef} {...provided.draggableProps}  >
                            <FavoritePreview
                                key={video.id.videoId}
                                // provided={provided}
                                video={video}
                                deleteFromList={deleteFromList}
                                setCurrVideo={setCurrVideo} />
                            // </div>
                            // )}
                            // </Draggable>
                        )
                    })}
                    {/* {provided.placeholder} */}
                </div>
                    }}
            {/* </Droppable> */}
