import React, { useEffect } from 'react'
import { useSelector } from 'react-redux';
import YouTube from 'react-youtube';
import { videoService } from '../services/videoService';

export function EmbeddedVideo({ video, saveToFavorites }) {
    const { favorites } = useSelector(state => state.listModule)

    // useEffect((video) => {
    //     console.log('isFavorites is:', isFavorites);
    // }, [favorites])

    const opts = {
        height: '510px',
        width: '100%',
        playerVars: {
            autoplay: 1,
        },
    }

    const _onReady = (event) => {
        event.target.pauseVideo();
    }
    if (!video) return <div>Loading..</div>
    return (

        <div className="video-player">
            <YouTube videoId={video.id?.videoId} opts={opts} onReady={_onReady} />
            <h1>{video.snippet?.title}</h1>
            <p>{video.snippet?.description}</p>
            {/* <p>Date: {video.snippet?.publishTime || video.snippet.publishedAt}</p> */}
            <button
                // className={ }
                onClick={() => saveToFavorites(video)}>Save</button>
        </div >

    )
}
