import React,{useState} from 'react'
import { Link, useHistory } from 'react-router-dom'
import logo from '../css/img/logo.png'

export function NavBar({ loadVideos, keyword, setKeyword, toggleMenuBar }) {
    const history = useHistory()
    const [word, setWord] = useState({})
    function setKeyWordInput(value){
        // setKeyword
        const keyWord = {txt:''}
        keyWord.txt = value
        setWord(keyWord)
    }

    return (
        <div className="nav-bar ">
            <div className="flex">
                <button
                    onClick={() => toggleMenuBar()}
                    className="hambur">‚â°</button>
                <Link to="/"><img src={logo} alt="" /></Link>
            </div>
            <form className="form" onSubmit={(ev) => loadVideos(ev)}>
                <input type="search" placeholder="Search" onChange={(ev) => setKeyWordInput(ev.target.value)} />
                <button onClick={history.push('/play')}>üîç</button>
            </form>
            <div className="info">
                <div className="avatar">A</div>
            </div>

        </div>
    )
}
