const initialState = {
    list: [],
    video: {},
    favorites: []
}

export default function ListReducer(state = initialState, action) {
    switch (action.type) {
        case 'SET_LIST':
            return {
                ...state,
                list: action.list
            }
        case 'SET_VIDEO':
            return {
                ...state,
                video: action.video
            }
        case 'SAVE_VIDEO':
            return {
                ...state,
                favorites: [action.video, ...state.favorites]
            }
        case 'UPDATE_FAVORITES':
            return {
                ...state,
                favorites: action.favorites
            }
        default: return state
    }

}
