import { VideoList } from '../cmps/VideoList';
import { saveVideo, setVideosList } from '../actions/ListAction'
import { useEffect, useState } from 'react';
import { EmbeddedVideo } from '../cmps/EmbeddedVideo';
import { NavBar } from '../cmps/NavBar';
import { useDispatch, useSelector } from 'react-redux';
import { videoService } from '../services/videoService';
// import { Route, Switch } from 'react-router-dom';
// import { Home } from './Home'

export function VideoApp() {
    const dispatch = useDispatch()
    const { list, favorites } = useSelector(state => state.listModule)
    const [video, setVideo] = useState({})
    const [keyword, setKeyword] = useState('')
    const [history, setHistory] = useState([])

    const [menu, setMenu] = useState(false)

    function toggleMenuBar() {
        setMenu(!menu)

    }
    async function loadVideos(ev = null) {
        if (ev)
            ev.preventDefault();
        dispatch(setVideosList(keyword))
    }
    const setCurrVideo = (video) => {
        // dispatch(setVideo(video.id.videoId))
        setVideo(video)
        const historyList = videoService.historylist(history, video)
        setHistory(historyList)
        console.log('menu is:', video);
    }
    const saveToFavorites = (video) => {
        dispatch(saveVideo(video))
    }
    useEffect(() => {
        console.log('favor', favorites);
    }, [favorites])
    return (
        <section>
            <NavBar
                loadVideos={loadVideos}
                toggleMenuBar={toggleMenuBar}
                setKeyword={setKeyword}
                keyword={keyword}
            />

            <div className="container flex">
                {menu &&
                    <div className="modal-bar">
                        <h1>Home page</h1>
                        <h1>Library</h1>
                        <h1>History</h1>
                        <h1>Videos I liked</h1>
                        <h1>{history}</h1>
                    </div>
                }
                <div className="video-app flex">
                    <VideoList list={list} setCurrVideo={setCurrVideo} />
                    <EmbeddedVideo video={video} saveToFavorites={saveToFavorites} />
                </div>
            </div>

        </section>
    )
}
